mapper.update:
UPDATE cropBacktracking.pickRecordInfo


pickTime=#{picktime}


pickType=#{picktype}


pickNum=#{picknum}


pickSeg_btCode=#{picksegBtcode}


comment=#{comment}


grower=#{grower}


sowSeg_btCode=#{sowsegBtcode}


  where  id=#{id}

mapper.select:
select * from cropBacktracking.pickRecord where pickSeg_btCode like '%${projectBtcode}%'
and( pickTime like '%${searchKey}%' or pickType like '%${searchKey}%' or pickNum like '%${searchKey}%' or pickSeg_btCode like '%${searchKey}%' or comment like '%${searchKey}%' or grower like '%${searchKey}%' or sowSeg_btCode like '%${searchKey}%' )
order by id limit #{startpage},#{rows}

select * from cropBacktracking.pickRecord where pickSeg_btCode like '${businessId}%'
and( pickTime like '%${searchKey}%' or pickType like '%${searchKey}%' or pickNum like '%${searchKey}%' or pickSeg_btCode like '%${searchKey}%' or comment like '%${searchKey}%' or grower like '%${searchKey}%' or sowSeg_btCode like '%${searchKey}%' )
order by id limit #{startpage},#{rows}
